import React, { useEffect } from "react";
import Chatbot from "./Chatbot";
import "./App.css";
import Chart from "chart.js/auto";

function App() {
    useEffect(() => {
        const ctx = document.getElementById("myChart");
        let myChart = null;

        if (myChart) {
            myChart.destroy(); // 销毁之前的图表实例
        }

        myChart = new Chart(ctx, {
            type: "bar",
            data: {
                labels: ["Red", "Blue", "Yellow", "Green", "Purple", "Orange"],
                datasets: [
                    {
                        label: "# of Votes",
                        data: [12, 19, 3, 5, 2, 3],
                        backgroundColor: [
                            "rgba(255, 99, 132, 0.2)",
                            "rgba(54, 162, 235, 0.2)",
                            "rgba(255, 206, 86, 0.2)",
                            "rgba(75, 192, 192, 0.2)",
                            "rgba(153, 102, 255, 0.2)",
                            "rgba(255, 159, 64, 0.2)",
                        ],
                        borderColor: [
                            "rgba(255, 99, 132, 1)",
                            "rgba(54, 162, 235, 1)",
                            "rgba(255, 206, 86, 1)",
                            "rgba(75, 192, 192, 1)",
                            "rgba(153, 102, 255, 1)",
                            "rgba(255, 159, 64, 1)",
                        ],
                        borderWidth: 1,
                    },
                ],
            },
            options: {
                scales: {
                    y: {
                        beginAtZero: true,
                    },
                },
            },
        });

        return () => {
            if (myChart) {
                myChart.destroy(); // 组件卸载时销毁图表实例
            }
        };
    }, []);

    return (
        <div className="app-container">
            <div className="chart-container">
                <canvas id="myChart" width="400" height="400"></canvas>
            </div>
            <div className="chatbot-container">
                <h1>AI Chatbot</h1>
                <Chatbot />
            </div>
        </div>
    );
}

export default App;
